var net = require('net');
var HOST = '192.168.0.200';
var PORT = 80;

const hexString=""
const rawHex = Buffer.from(hexString, 'hex');
var client = new net.Socket();
client.connect(PORT, HOST, () => {
    console.log("Connected");
    client.write(rawHex); //This will send the byte buffer over TCP
})

